  `id` int(11) NOT NULL AUTO_INCREMENT,
  `url` varchar(145) COLLATE utf8_unicode_ci DEFAULT NULL,
  `title` varchar(225) COLLATE utf8_unicode_ci DEFAULT NULL,
  `intro` text COLLATE utf8_unicode_ci,
  `content` text COLLATE utf8_unicode_ci,
  `date` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `picture` varchar(145) COLLATE utf8_unicode_ci DEFAULT NULL,
  `articleId` int(11) DEFAULT NULL,
  `pageId` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `articlesId_UNIQUE` (`articleId`),
  FULLTEXT KEY `Search` (`content`,`intro`,`title`)
) ENGINE=MyISAM AUTO_INCREMENT=57 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
