INSERT INTO `settings` VALUES ('seoDescription','Сайт за футбол и прогнози на футболни мачове, и всичко свързано с тях. Цялата информация от която се нуждаете за мачовете от елитните европейски първенства. Прогнози и статии за вашето успешно залагане.'),('seoKeywords','футбол,прогнози,футболни прогнози,статии,предвиждания,европейски първенства,Англия,Италия,Испания,Германия,Висша лига,Бундеслига,Серия А,Примиера Дивисион,Мачове на живо,preview,футболни отбори,залагания,футболни мачове'),('seoRobots','User-agent: *\nDisallow: /ckfinder\nDisallow: /ckeditor');
